import React, { useState } from 'react';
import axios from 'axios';

function RegistroUsuario({ setMostrarRegistro }) {
    const [usuario, setUsuario] = useState({ nombres: '', correo: '', clave: '' });
    const [mensaje, setMensaje] = useState('');

    const handleChange = (e) => {
        setUsuario({ ...usuario, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:8080/api/v1/registro', usuario);
            setMensaje('Usuario registrado correctamente.');
            console.log(response.data);
        } catch (error) {
            setMensaje('Error al registrar usuario. Por favor, verifica tus datos.');
            console.error(error);
        }
    };

    return (
        <div>
            <h2>Registro de Usuario</h2>
            <form onSubmit={handleSubmit}>
                <input type="text" name="nombres" value={usuario.nombres} onChange={handleChange} placeholder="Nombre completo" required />
                <input type="email" name="correo" value={usuario.correo} onChange={handleChange} placeholder="Correo electrónico" required />
                <input type="password" name="clave" value={usuario.clave} onChange={handleChange} placeholder="Contraseña" required />
                <button type="submit">Registrarse</button>
                <p>Ya tienes una cuenta? <span onClick={() => setMostrarRegistro(false)}>Iniciar sesión</span></p>
            </form>
            {mensaje && <p>{mensaje}</p>}
        </div>
    );
}

export default RegistroUsuario;
