// LoginUsuario.js
import React, { useState } from 'react';
import axios from 'axios';

function LoginUsuario() {
    const [credenciales, setCredenciales] = useState({ email: '', correo: '' });
    const [mensaje, setMensaje] = useState('');

    const handleChange = (e) => {
        setCredenciales({ ...credenciales, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:8080/api/v1/login', credenciales);
            console.log(response.data);
            setMensaje('Bienvenido.');
        } catch (error) {
            console.error(error);
            setMensaje('Error al registrar usuario. Por favor, verifica tus datos.');
        }
    };

    return (
        <div>
            <h2>Login de Usuario</h2>
            <form onSubmit={handleSubmit}>
                <input type="email" name="email" value={credenciales.email} onChange={handleChange} placeholder="Correo electrónico" />
                <input type="password" name="correo" value={credenciales.correo} onChange={handleChange} placeholder="Contraseña" />
                <button type="submit">Iniciar sesión</button>
                {mensaje && <p>{mensaje}</p>}
            </form>
        </div>
    );
}

export default LoginUsuario;
